DbpediaEntity.create("id"=>158, "name"=>"Museum of Fine Arts, Boston", "url"=>"http://dbpedia.org/resource/Museum_of_Fine_Arts,_Boston", "description"=>"The Museum of Fine Arts in Boston, Massachusetts, is one of the largest museums in the United States, attracting over one million visitors a year. It contains over 450,000 works of art, making it one of the most comprehensive collections in the Americas. It is also the 54th most visited art museum in the world, and the twelfth most-visited in the United States, as of 2010. The museum was founded in 1870 and its current location dates to 1909.", "refCount"=>577, "stanford_type"=>"organization", "stanford_name"=>"Museum of Fine Arts")
DbpediaEntity.create("id"=>159, "name"=>"Harvard Art Museums", "url"=>"http://dbpedia.org/resource/Harvard_Art_Museums", "description"=>"The Harvard Art Museums, part of Harvard University, comprise three museums and four research centers (Straus Center for Conservation and Technical Studies, the Center for the Technical Study of Modern Art, the Harvard Art Museums Archives, and the Archaeological Exploration of Sardis). The Harvard Art Museums are distinguished by the range and depth of their collections, their exhibitions, and the original research of their staff.", "refCount"=>55, "stanford_type"=>"organization", "stanford_name"=>"Harvard Art Museums")
