DbpediaEntity.create("id"=>474, "name"=>"Maida Vale", "url"=>"http://dbpedia.org/resource/Maida_Vale", "description"=>"Maida Vale is a residential district comprising the northern part of Paddington in west London, between St John's Wood and Kilburn. It is part of the City of Westminster. The area is mostly residential, and mainly affluent, consisting of many large late Victorian and Edwardian blocks of mansion flats. It is also home to the BBC Maida Vale Studios.", "refCount"=>181, "stanford_type"=>"person", "stanford_name"=>"Maida")
