DbpediaEntity.create("id"=>483, "name"=>"Torah", "url"=>"http://dbpedia.org/resource/Torah", "description"=>"The Torah is the Jewish name for the first five books of the Jewish Bible. In Hebrew the five books are named by the first phrase in the text: Bereshit (\"In the beginning,\" Book of Genesis), Shemot, Vayikra, Bamidbar (\"In the desert,\" Numbers) and Devarim. In rabbinic literature the word Torah denotes both these five books, Torah Shebichtav (תורה שבכתב, \"Torah that is written\"), and an Oral Torah, Torah Shebe'al Peh (תורה שבעל פה, \"Torah that is spoken\").", "refCount"=>2909, "stanford_type"=>"person", "stanford_name"=>"Moses")
